mapper.insert:
INSERT INTO FILE_GRP_TB(
	MEM_FK, AUTH_GRP_FK, FILE_GRP_REG_DT
)VALUES(
	#{memNo}, #{authGrpNo}, CURRENT_TIMESTAMP()
)

	SELECT LAST_INSERT_ID()

INSERT INTO TEMP_SAVE_TB(
	MEM_FK, FILE_GRP_FK, TEMP_SAVE_CATEGORY, TEMP_SAVE_TITLE, TEMP_SAVE_BODY
)VALUES(
	#{memNo}, #{fileGrpNo}, #{tempSaveCategory.value}, #{tempSaveTitle}, #{tempSaveBody}
)

	SELECT LAST_INSERT_ID()

mapper.select:
SELECT	FILE_GRP_FK, MEM_FK
FROM	TEMP_SAVE_TB
WHERE	TEMP_SAVE_SQ_PK = #{tempSaveNo}

SELECT * FROM( SELECT M.MEM_SQ_PK, M.MEM_NICKNM, M.MEM_ID,
FG.FRBRD_GRP_SQ_PK, FG.FILE_GRP_FK, FG.CMT_GRP_FK, FG.AUTH_GRP_FK,
F.FRBRD_SQ_PK, F.FRBRD_REG_DT, F.FRBRD_TITLE, F.FRBRD_BODY FROM FRBRD_GRP_TB AS FG INNER JOIN MEM_TB AS M ON(FG.MEM_FK = M.MEM_SQ_PK) INNER JOIN FRBRD_TB AS F ON(FG.FRBRD_GRP_SQ_PK = F.FRBRD_GRP_FK) WHERE FG.FRBRD_GRP_SQ_PK = #{freeboardGroupNo} ORDER BY F.FRBRD_SQ_PK DESC ) AS T LIMIT 1;
