mapper.insert:

INSERT INTO FRBRD_GRP_TB(
	MEM_FK,
	CMT_GRP_FK,
	AUTH_GRP_FK
)VALUES(
	#{memNo},
	#{commentGroupNo},
	#{authgroup}
)

	SELECT LAST_INSERT_ID()

mapper.select:
SELECT * FROM( SELECT M.MEM_SQ_PK, M.MEM_NICKNM, M.MEM_ID,
FG.FRBRD_GRP_SQ_PK, FG.CMT_GRP_FK, FG.AUTH_GRP_FK, CG.CMT_GRP_NEW_WRT_FL,
F.FRBRD_SQ_PK, F.FRBRD_REG_DT, F.FILE_GRP_FK, F.FRBRD_TITLE, F.FRBRD_BODY FROM FRBRD_GRP_TB AS FG INNER JOIN MEM_TB AS M ON(FG.MEM_FK = M.MEM_SQ_PK) INNER JOIN FRBRD_TB AS F ON(FG.FRBRD_GRP_SQ_PK = F.FRBRD_GRP_FK) INNER JOIN CMT_GRP_TB CG ON(FG.CMT_GRP_FK = CG.CMT_GRP_SQ_PK) WHERE FG.FRBRD_GRP_SQ_PK = #{freeboardGroupNo} ORDER BY F.FRBRD_SQ_PK DESC ) AS T LIMIT 1;

SELECT MEM_FK
FROM FRBRD_GRP_TB
WHERE FRBRD_GRP_SQ_PK = #{freeboardGroupNo}

SELECT CMT_GRP_FK
FROM FRBRD_GRP_TB
WHERE FRBRD_GRP_SQ_PK = #{freeboardGroupNo}

mapper.update:
UPDATE FRBRD_GRP_TB
SET AUTH_GRP_FK = #{authgroup}
WHERE FRBRD_GRP_SQ_PK = #{freeboardGroupNo}
