mapper.insert:

INSERT INTO PST_GRP_TB(
	MEM_FK,
	CMT_GRP_FK,
	PST_OP_TYPE_FK
)VALUES(
	#{memNo},
	#{commentGroupNo},
	#{openType}
)

	SELECT LAST_INSERT_ID()

mapper.select:
SELECT * FROM( SELECT M.MEM_SQ_PK, M.MEM_NICKNM, M.MEM_ID,
FG.PST_GRP_SQ_PK, FG.CMT_GRP_FK, FG.PST_OP_TYPE_FK, CG.CMT_GRP_NEW_WRT_FL,
F.PST_SQ_PK, F.PST_REG_DT, F.FILE_GRP_FK, F.PST_TITLE, F.PST_BODY FROM PST_GRP_TB AS FG INNER JOIN MEM_TB AS M ON(FG.MEM_FK = M.MEM_SQ_PK) INNER JOIN PST_TB AS F ON(FG.PST_GRP_SQ_PK = F.PST_GRP_FK) INNER JOIN CMT_GRP_TB CG ON(FG.CMT_GRP_FK = CG.CMT_GRP_SQ_PK) WHERE FG.PST_GRP_SQ_PK = #{freeboardGroupNo} ORDER BY F.PST_SQ_PK DESC ) AS T LIMIT 1;

SELECT MEM_FK
FROM PST_GRP_TB
WHERE PST_GRP_SQ_PK = #{freeboardGroupNo}

SELECT CMT_GRP_FK
FROM PST_GRP_TB
WHERE PST_GRP_SQ_PK = #{freeboardGroupNo}

mapper.update:
UPDATE PST_GRP_TB
SET PST_OP_TYPE_FK = #{openType}
WHERE PST_GRP_SQ_PK = #{freeboardGroupNo}
